function styleInject(css, ref) {
  if ( ref === void 0 ) ref = {};
  var insertAt = ref.insertAt;

  if (!css || typeof document === 'undefined') { return; }

  var head = document.head || document.getElementsByTagName('head')[0];
  var style = document.createElement('style');
  style.type = 'text/css';

  if (insertAt === 'top') {
    if (head.firstChild) {
      head.insertBefore(style, head.firstChild);
    } else {
      head.appendChild(style);
    }
  } else {
    head.appendChild(style);
  }

  if (style.styleSheet) {
    style.styleSheet.cssText = css;
  } else {
    style.appendChild(document.createTextNode(css));
  }
}

export default styleInject;
import styles from '../styles.module.css'

interface CursorProps {
  cursorStyle?: string
  cursorColor?: string
}

export const Cursor = ({
  cursorStyle = '|',
  cursorColor = 'inherit'
}: CursorProps): JSX.Element => {
  return (
    <span style={{ color: cursorColor }} className={styles.blinkingCursor}>
      {cursorStyle}
    </span>
  )
}
export type State = {
  speed: number
  text: string
  isDeleting: boolean
  count: number
}

export type Action =
  | { type: 'SPEED'; payload: number }
  | { type: 'TYPE'; payload: string; speed: number }
  | { type: 'DELETE'; payload: string; speed: number }
  | { type: 'COUNT' }

export function reducer(state: State, action: Action): State {
  switch (action.type) {
    case 'SPEED':
      return {
        ...state,
        isDeleting: true,
        speed: action.payload
      }
    case 'TYPE':
      return {
        ...state,
        speed: action.speed,
        text: action.payload?.substring(0, state.text.length + 1)
      }
    case 'DELETE':
      return {
        ...state,
        speed: action.speed,
        text: action.payload?.substring(0, state.text.length - 1)
      }
    case 'COUNT':
      return {
        ...state,
        isDeleting: false,
        count: state.count + 1
      }
    default:
      return state
  }
}
import { useCallback, useEffect, useRef, useReducer } from 'react'
import { reducer } from '../reducer'

export interface TypewriterProps {
  /** Callback Function that is triggered when loops are completed. available if loop is > 0 */
  onLoopDone?: () => void
  /** Callback Function that is runs while typing */
  onType?: (counter: number) => void
  /** Array of strings holding the words */
  words: string[]
  /** Control how many times to run. `0 | false` to run infinitely */
  loop?: number | boolean
  /** Character typing speed in Milliseconds */
  typeSpeed?: number
  /** Character deleting speed in Milliseconds */
  deleteSpeed?: number
  /** Delay time between the words in Milliseconds */
  delaySpeed?: number
}

export const useTypewriter = ({
  words = ['Hello World!', 'This is', 'a simple Typewriter'],
  loop = 1,
  typeSpeed = 80,
  deleteSpeed = 50,
  delaySpeed = 1500,
  onLoopDone,
  onType
}: TypewriterProps): (string | number)[] => {
  const [{ isDeleting, speed, text, count }, dispatch] = useReducer(reducer, {
    isDeleting: false,
    speed: typeSpeed,
    text: '',
    count: 0
  })

  // Refs
  const loops = useRef(0)
  const isDone = useRef(false)

  const handleTyping = useCallback(() => {
    const index = count % words.length
    const fullWord = words[index]

    if (!isDeleting) {
      dispatch({ type: 'TYPE', payload: fullWord, speed: typeSpeed })

      if (onType) onType(count)

      if (text === fullWord) {
        dispatch({ type: 'SPEED', payload: delaySpeed })

        if (loop > 0) {
          loops.current += 1
          if (loops.current / words.length === loop) isDone.current = true
        }
      }
    } else {
      dispatch({ type: 'DELETE', payload: fullWord, speed: deleteSpeed })

      if (text === '') dispatch({ type: 'COUNT' })
    }
  }, [
    isDeleting,
    count,
    delaySpeed,
    deleteSpeed,
    loop,
    typeSpeed,
    words,
    text,
    onType
  ])

  useEffect(() => {
    const typing = setTimeout(handleTyping, speed)

    if (isDone.current) {
      clearTimeout(typing)
      if (onLoopDone) onLoopDone()
    }

    return () => clearTimeout(typing)
  }, [handleTyping, speed, onLoopDone])

  return [text, count + 1]
}
import { Cursor } from './Cursor'
import { TypewriterProps, useTypewriter } from '../Hooks/useTypewriter'

interface ComponentProps extends TypewriterProps {
  /** Show / Hide the cursor */
  cursor?: boolean
  /** Change the cursor style available if cursor is enabled */
  cursorStyle?: string
  /** Change the cursor Color */
  cursorColor?: string
}
/**
 * A Simple React Component for adding a nice Typewriter effect to your project.
 */
export const Typewriter = ({
  words = ['Hello World!', 'This is', 'a simple Typewriter'],
  loop = 1,
  typeSpeed = 80,
  deleteSpeed = 50,
  delaySpeed = 1500,
  cursor = false,
  cursorStyle = '|',
  cursorColor = 'inherit',
  onLoopDone,
  onType
}: ComponentProps): JSX.Element => {
  const [text] = useTypewriter({
    words,
    loop,
    typeSpeed,
    deleteSpeed,
    delaySpeed,
    onLoopDone,
    onType
  })

  return (
    <>
      <span>{text}</span>
      {cursor && <Cursor cursorStyle={cursorStyle} cursorColor={cursorColor} />}
    </>
  )
}
